mapper.select:
		select * from (
    		select tmp.*, rownum rn from (
        		select p.*, i.attachment_no from dang_place p INNER JOIN place_img i on p.place_no = i.place_no order by place_name
    		)TMP
		) where rn between #{startRow} and #{endRow}

		select * from (
    		select tmp.*, rownum rn from (
        		select p.*, i.attachment_no from dang_place p INNER JOIN place_img i on p.place_no = i.place_no where instr(${type},#{keyword})>0 order by place_name
    		)tmp
		) where rn between #{startRow} and #{endRow}

SELECT * FROM (
	SELECT * FROM recommend_place ORDER BY DBMS_RANDOM.VALUE
)WHERE ROWNUM   < 4
